mapper.select:

select * from (
	select a.firstdate,b.recipe_num,b.recipe_title,b.recipe_img,b.recipe_food_main,b.recipe_food_suv,b.excel,a.user_num,rownum rn from 
	(select * from markrecipe_tb where user_num = #{userNum} order by firstdate desc) a 
	inner join 
	( select * from 
	    ( select * from  recipe_basic_tb)
	     UNION ALL
	    ( select * from excel_recipe_basic_tb)
	) b ON (a.recipe_num = b.recipe_num and a.excel = b.excel) where rownum  <=   #{startNum}+4
) where rn > #{startNum}
