mapper.update:


update  recipegram_tb set recipegram_content = #{recipegram_content}, 
							updatedate = #{updatedate}, 
							recipegram_secret = #{recipegram_secret}
				where recipegram_num = #{recipegram_num}

mapper.select:
SELECT recipegram_num FROM ( SELECT recipegram_num, rownum r FROM ( SELECT recipegram_num FROM ( SELECT recipegram_num, user_num, user_nickname, recipegram_content, recipegram_like_cnt, firstdate, updatedate, recipegram_secret, img_num, img_name FROM ( SELECT rg.recipegram_num, rg.user_num, det.user_nickname, recipegram_content, recipegram_like_cnt, rg.firstdate, rg.updatedate, recipegram_secret, img_num, img_name FROM
recipegram_tb rg LEFT OUTER JOIN user_tb mem on mem.user_num=rg.user_num LEFT OUTER JOIN img_tb img on rg.recipegram_num=img.recipegram_num LEFT OUTER JOIN user_detail_tb det on det.user_num=rg.user_num LEFT OUTER JOIN hash_tb hash on rg.recipegram_num=hash.recipegram_num )
)group by recipegram_num order by recipegram_num desc )where rownum<= (#{startNum} * 3)+3 )where r>#{startNum} * 3

SELECT recipegram_num, user_num, user_nickname, recipegram_content, recipegram_like_cnt, firstdate, updatedate, recipegram_secret, img_num, img_name FROM ( SELECT rg.recipegram_num, rg.user_num, det.user_nickname, recipegram_content, recipegram_like_cnt, rg.firstdate, rg.updatedate, recipegram_secret, img_num, img_name FROM
recipegram_tb rg LEFT OUTER JOIN user_tb mem on mem.user_num=rg.user_num LEFT OUTER JOIN img_tb img on rg.recipegram_num=img.recipegram_num LEFT OUTER JOIN user_detail_tb det on det.user_num=rg.user_num LEFT OUTER JOIN hash_tb hash on rg.recipegram_num=hash.recipegram_num
)where recipegram_num = #{recipegram_num}
